import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import './App.css';
import HomePage from "./component/homepage/homepage";
import Login  from "./component/login/login";
import Register from "./component/register/register";
import Profile from "./component/userProfile/userProfile"

class App extends Component {

  alertClass (type) {
    let classes = {
      error_msg: 'alert-danger',
      error: 'alert-danger',
      success_msg: 'alert-success'
    };
    return classes[type] || classes.success_msg;
  }

  render() {
    const message = this.props.message;
    const alertClassName = `alert ${this.alertClass(message.type)} fade in`;

    return (
      <Router>
        <div className={alertClassName}>
          <button className='close' data-dismiss='alert'> &times; </button>
          {message.text}
        </div>
        <div>
          {/* <Nav /> */}
          <Switch>
            <Route exact path="/" component={HomePage} />
            <Route exact path="/login" component={Login} />
            <Route exact path="/register/candidate" component={Register} />
            <Route exact path="/userProfile" component={Profile} />            
          </Switch>
        </div>
      </Router>
    )
  }
}

export default App;
